import React from 'react';
import club from './club.jpg';
import diamond from './diamond.jpg';
import heart from './heart.jpg';
import spade from './spade.jpg';

class Blackjack extends React.Component{

  constructor(props) {
    super(props);
    this.state = {
      cards52: [
        { suit: "club" , number: "A" },
        { suit: "club" , number: "2" },
        { suit: "club" , number: "3" },
        { suit: "club" , number: "4" },
        { suit: "club" , number: "5" },
        { suit: "club" , number: "6" },
        { suit: "club" , number: "7" },
        { suit: "club" , number: "8" },
        { suit: "club" , number: "9" },
        { suit: "club" , number: "10" },
        { suit: "club" , number: "J" },
        { suit: "club" , number: "Q" },
        { suit: "club" , number: "K" }
      ]
    };
  }

  changeSuit = () => {
    this.setState({
      cards52: {
        suit: "club",
        number: this.state.cards52.number
      }
    });
  }

  render(){
    let suitpic = club
    if(this.state.cards52.suit === "club"){
      suitpic = club
    } else if (this.state.cards52.suit === "diamond"){
      suitpic = diamond
    } else if (this.state.cards52.suit === "heart"){
      suitpic = heart
    } else {
      suitpic = spade
    }

    return(
      <div style = {{height: "100px", width: "50px", padding: "50px", border: "1px solid black"}}>
        <img src = {suitpic} alt="suit" width="40px" height="80px"/>
        {this.state.cards52.number}
        <br/><br/><br/><br/><br/><br/>
        <button id="suitButton" name="club" onClick={this.changeSuit}>Clubs</button>
        <h4>Yo!</h4>
      </div>

    );
  }
}

export default Blackjack;
