import React from 'react';
import club from './club.jpg';
import diamond from './diamond.jpg';
import heart from './heart.jpg';
import spade from './spade.jpg';

class Blackjack extends React.Component{

  constructor(props) {
    super(props);
    this.state = {
      cards52: [
        { suit: "club" , number: "A" },
        { suit: "club" , number: "2" },
        { suit: "club" , number: "3" },
        { suit: "club" , number: "4" },
        { suit: "club" , number: "5" },
        { suit: "club" , number: "6" },
        { suit: "club" , number: "7" },
        { suit: "club" , number: "8" },
        { suit: "club" , number: "9" },
        { suit: "club" , number: "10" },
        { suit: "club" , number: "J" },
        { suit: "club" , number: "Q" },
        { suit: "club" , number: "K" }
      ],
      suit: 'club',
      number: '',
    };
  }

  randomCard = () => {
    let random = Math.floor(Math.random() * 10);
    this.setState({
        suit: this.state.cards52[random].suit,
        number: this.state.cards52[random].number,
    });
    console.log(random)
    console.log(this.state.suit)
    console.log(this.state.number)
  }

  render(){
    let suitpic = club
    if(this.state.cards52.suit === "club"){
      suitpic = club
    } else if (this.state.cards52.suit === "diamond"){
      suitpic = diamond
    } else if (this.state.cards52.suit === "heart"){
      suitpic = heart
    } else {
      suitpic = spade
    }

    return(
      <div style = {{height: "100px", width: "50px", padding: "50px", border: "1px solid black"}}>
        <img src = {suitpic} alt="suit" width="40px" height="80px"/>
        {this.state.cards52.number}
        <br/><br/><br/><br/><br/><br/>
        <button onClick={this.randomCard}>Generate Random Card!</button>
      </div>
    );
  }
}

export default Blackjack;
