import React from 'react';
import club from './club.jpg';
import diamond from './diamond.jpg';
import heart from './heart.jpg';
import spade from './spade.jpg';
import { Link } from 'react-router-dom';

class Homepage extends React.Component{

  constructor(props) {
    super(props);
    this.state = {
      cards52:
        { suit: "spade" , number: "5" }
    };
  }

  changeSuit = () => {
    this.setState({
      cards52: {
        suit: "club",
        number: this.state.cards52.number
      }
    });
  }

  render(){
    let suitpic = club
    if(this.state.cards52.suit === "club"){
      suitpic = club
    } else if (this.state.cards52.suit === "diamond"){
      suitpic = diamond
    } else if (this.state.cards52.suit === "heart"){
      suitpic = heart
    } else {
      suitpic = spade
    }

    return(
      <div>
        <div>
          <h1>Welcome to the Quarantine Games</h1>

          <nav class="navbar navbar-expand-lg navbar-light bg-light">
            <a class="navbar-brand" href="#">Navbar</a>
            <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
              <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarSupportedContent">
              <ul class="navbar-nav mr-auto">
                <li class="nav-item dropdown">
                  <Link class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                    Card Games
                  </Link>
                  <div class="dropdown-menu" aria-labelledby="navbarDropdown">
                    <a class="dropdown-item" href="#">Action</a>
                    <a class="dropdown-item" href="#">Another action</a>
                    <div class="dropdown-divider"></div>
                    <a class="dropdown-item" href="#">Something else here</a>
                  </div>
                </li>
              </ul>
            </div>
          </nav>

          <nav class="nav">
            <Link to="/blackjack" class="nav-link active">1. Blackjack</Link>
            <Link to="/poker" class="nav-link">2. Poker</Link>
            <Link to="/TarunQuiz" class="nav-link">3. Tarun Quiz</Link>
            <Link to="/taboo" class="nav-link">4. Taboo</Link>
            <Link to="/set" class="nav-link">5. Set</Link>
          </nav>
        </div>
        <br/>
        <div style = {{height: "100px", width: "50px", padding: "50px", border: "1px solid black"}}>
          <img src = {suitpic} alt="suit" width="40px" height="80px"/>
          {this.state.cards52.number}
        </div>
        <br/><br/><br/><br/><br/><br/>
        <button id="suitButton" name="club" onClick={this.changeSuit}>Clubz for no reason</button>
      </div>
    );
  }
}

export default Homepage;
